mapper.select:
SELECT t.*, t.color_name as color, t1.color_hex, tb.color AS quote FROM t_basicsdatum_material_color t LEFT JOIN t_goods_color t1 ON t.company_code = t1.company_code AND t.color_code = t1.color_code and t1.del_flag = '0' LEFT JOIN t_basicsdatum_material_price tb ON tb.color LIKE CONCAT('%', t.color_code, '%') WHERE t.company_code = #{companyCode} and t.del_flag = '0' and t.material_code = #{materialCode}
and t.status=#{status}
GROUP BY t.id

SELECT t.*, tb.width AS quote FROM t_basicsdatum_material_width t LEFT JOIN t_basicsdatum_specification t1 ON t.company_code = t1.company_code AND t.width_code = t1.`code` AND t1.del_flag = '0' LEFT JOIN t_basicsdatum_material_price tb ON tb.width LIKE CONCAT('%', t.width_code, '%') WHERE t.company_code = #{companyCode} and t.del_flag = '0' and t.material_code = #{materialCode}
and t.status=#{status}
GROUP BY t.id
