mapper.select:
	select
        tfbi.id,
        tfbi.fabric_detailed_id,
        tfbi.year,
        tfbi.brand,
        tfbi.season,
        tfbi.manufacturer,
        tfbi.manufacturer_number,
        tfbi.manufacturer_colour,
        tfbi.is_new_fabric,
        tfbi.quantity,
        tfbi.register_date,
        tfdi.id tfdi_id,
        tfdi. fabric_is_usable,
        tfdi.  fabric_price,
        tfdi. specification,
        tfdi. density,
        tfdi. ingredient,
        tfdi.  leadtime,
        tfdi. minimum_order_quantity,
        tfdi. larghezza,
        tfdi.  gram_weight,
        tfdi. germinal_condition,
        tfdi. atactiform_date,
        tfdi. estimate_atactiform_date,
        tfdi.practical_atactiform_date,
        tfdi. inspect_date,
        tfdi. physicochemistry_detection_result,
        tfdi. sample_washing_inspection_date,
        tfdi. wash_detection_result,
        tfdi.image_url,
        tfdi.report_url
    from t_fabric_basic_information tfbi
    left join t_fabric_detailed_information tfdi on tfbi.fabric_detailed_id=tfdi.id  and tfdi.is_draft='1'
    left join t_fabric_job tfj on tfbi.id=tfj.fabric_basic_id
     where tfbi.company_code=#{companyCode}
        and tfbi.del_flag='0'
        and tfj.user_job_id in

            #{idItem}

        and tfj.sponsor_status=#{originate}

        and tfbi.manufacturer_number like  concat('%',#{manufacturerNumber},'%')


        and tfbi.manufacturer=#{manufacturer}


            and tfbi.year=#{year}


            and tfbi.season=#{season}


            and tfbi.manufacturer_colour=#{manufacturerColour}


            and tfbi.is_new_fabric=#{isNewFabric}


            and tfbi.quantity=#{quantity}


            and ( tfbi.register_date between #{registerDate.[0]} and #{registerDate.[1]}
            or tfdi.atactiform_date between #{registerDate.[0]} and #{registerDate.[1]}
            or tfdi.estimate_atactiform_date between #{registerDate.[0]} and #{registerDate.[1]}
            or tfdi.practical_atactiform_date between #{registerDate.[0]} and #{registerDate.[1]}
            or tfdi.inspect_date between #{registerDate.[0]} and #{registerDate.[1]}
            or tfdi.estimate_atactiform_date between #{registerDate.[0]} and #{registerDate.[1]}
            or tfdi.sample_washing_inspection_date between #{registerDate.[0]} and #{registerDate.[1]}
            )


            and tfdi.fabric_price  between #{fabricPrice.[0]} and #{fabricPrice.[1]}



            and tfdi.ingredient   like  concat('%',#{ingredient},'%')




            and tfdi.leadtime like  concat('%',#{leadtime},'%')



            and tfdi.germinal_condition=#{germinalCondition}



            and tfdi.fabric_is_usable=#{fabricIsUsable}



            and tfdi.physicochemistry_detection_result=#{physicochemistryDetectionResult}



            and tfdi.wash_detection_result=#{washDetectionResult}

        order By tfbi.create_date desc
