mapper.select:
SELECT CONCAT( tsd.design_no, tsd.style_name ) AS style, tsd.devt_type AS produceType, tsd.style_type AS styleType, tsd.size_range_name AS modelTypeName, tsd.design_no AS designNo, tssc.style_no AS bulkStyleNo, tssc.color_name AS color, tssc.tag_price AS tagPrice, tbmt.model_Type AS modelType, ht.id, ht.confirm_date, ht.product_name AS productName, ht.product_code AS productCode, ht.execute_standard_code, ht.execute_standard, ht.safty_title, ht.safty_title_code, ht.safty_type_code, ht.safty_type, ht.quality_grade, GROUP_CONCAT( hti.type, '(', hti.percentage, ')' ) AS ingredient_b, ht.ingredient, ht.warm_tips, ht.washing_label, ht.storage_demand, ht.producer, ht.down_content, ht.special_spec, ht.washing_material_remarks, ht.grading_name, ht.technologist_name, ht.sample_maker_name, ht.place_order_staff_name, ht.place_order_date, ht.STATUS FROM t_style_color tssc JOIN t_style tsd ON tssc.style_id = tsd.id LEFT JOIN t_hang_tag ht ON tssc.style_no = ht.bulk_style_no LEFT JOIN t_hang_tag_ingredient hti ON ht.id = hti.hang_tag_id LEFT JOIN t_basicsdatum_model_type tbmt ON tsd.size_range = tbmt.id
tssc.del_flag = 0 AND tssc.company_code = #{dto.companyCode}
AND tsd.style_no like concat('%',#{dto.style},'%')
AND tssc.style_no like concat('%',#{dto.bulkStyleNo},'%')
AND ht.ingredient like concat('%',#{dto.ingredient},'%')
AND (ht.status = '' or ht.status = '0' or ht.status is null)
AND ht.status = #{dto.status}
AND ht.confirm_date like concat('%',#{dto.confirmDate},'%')
AND tsd.design_no = #{dto.designNo}
AND tssc.style_no like concat('%',#{styleNo},'%')
AND tssc.style_no = #{styleNo}
GROUP BY tssc.style_no

SELECT tssc.color_name AS ColorDescription, tssc.color_code AS ColorCode, tssc.style_no AS StyleCode, tssc.tag_price AS C8_Colorway_SalesPrice, tsd.subject as Theme, tsd.prod_category AS C8_Collection_ProdCategory, tsd.prod_category3rd AS C8_Style_3rdCategory, tsd.prod_category2nd AS C8_Style_2ndCategory, tsd.size_range AS SizeRangeCode, tsd.size_range_name AS SizeRangeName, tsd.dev_class AS ProductType, tsd.prod_category1st AS C8_1stProdCategory, tsd.size_range_name AS SizeRangeDimensionType, GROUP_CONCAT( hti.type, '(', hti.percentage, ')' ) AS Composition, ht.washing_label AS CareSymbols, ht.quality_grade AS QualityClass, ht.quality_grade AS ProductName, ht.safty_type AS SaftyType, ht.execute_standard AS OPStandard, ht.warm_tips AS Attention, ht.safty_title AS SaftyTitle, ht.washing_material_remarks AS C8_APPBOM_Comment, ht.storage_demand AS StorageRequirement, ht.storage_demand AS StorageRequirement FROM t_style_color tssc JOIN t_style tsd ON tssc.style_id = tsd.id LEFT JOIN t_hang_tag ht ON tssc.style_no = ht.bulk_style_no LEFT JOIN t_hang_tag_ingredient hti ON ht.id = hti.hang_tag_id where tssc.del_flag = 0
AND tssc.style_no = #{styleNo}
GROUP BY tssc.style_no
