mapper.select:
select tm.*,tmd.*,tmc.id as collectId
from t_material tm
left join t_material_details tmd on tm.id = tmd.material_id
left join t_material_collect tmc on tm.id = tmc.material_id and tmc.del_flag = '0'
where tm.del_flag = '0'

and material_library = #{materialLibrary}
and tm.company_code=#{companyCode}

    and tm.id in

        #{id}



    and status in

        #{item}



    and material_name like concat('%',#{materialName},'%')


    and material_type = #{materialType}


    and tm.create_id = #{createId}

order by create_date desc
