mapper.select:
select tm.*,tmc.id as collectId
from t_material tm
left join t_material_collect tmc on tm.id = tmc.material_id and tmc.del_flag = '0'
where tm.del_flag = '0'
and material_category_id = #{materialCategoryId}
and tm.company_code = #{companyCode}

    and tm.id in

        #{id}



    and status in

        #{item}



    and file_info like concat('%',#{fileInfo},'%')


    and category_id = #{categoryId}


    and material_subcategory like concat('%',#{materialSubcategory},'%')


    and year = #{year}


    and month = #{month}


    and season = #{season}


    and tm.create_id = #{createId}

order by create_date desc
