mapper.select:
select id
from t_pack_bom
where bom_version_id = #{bomVersionId}

SELECT * FROM ( SELECT bc.id AS color_id, b.id AS book_id, bc.order_book_code, bc.style_no, t1.design_no, b.meet_flag, b.lock_flag FROM t_style_order_book_color bc LEFT JOIN t_style_order_book b ON bc.order_book_code = b.order_book_code LEFT JOIN t_style_group sg ON bc.style_no = sg.style_no AND sg.del_flag = '0' LEFT JOIN t_style_color t1 ON bc.style_no = t1.style_no AND t1.del_flag = '0' LEFT JOIN t_style t2 ON t1.style_id = t2.id AND t2.del_flag = '0' WHERE bc.del_flag = '0' AND b.meet_flag = '1' AND bc.company_code = #{companyCode} ) cbgcd LEFT JOIN ( SELECT pb.material_id AS id, pb.material_name, bm.image_url, bm.category_name, bm.supplier_id, bm.supplier_name, pb.supplier_material_code, pb.material_code, bm.factory_composition, pb.part_code, pb.part_name, bm.translate, pb.color, pb.remarks, bm.purchase_unit_name, pb.price, pb.loss_rate, pb.cost, pb.supplier_price, ssc.design_no AS design_no_two, ssc.style_no AS style_no_two, sd.MONTH, sd.band_code FROM t_pack_bom pb LEFT JOIN t_pack_bom_version pbv on pbv.id = pb.bom_version_id LEFT JOIN t_basicsdatum_material bm ON bm.id = pb.material_id LEFT JOIN t_pack_info pi ON pi.id = pb.foreign_id AND pi.del_flag = '0' LEFT JOIN t_style sd ON sd.id = pi.foreign_id AND sd.del_flag = '0' LEFT JOIN t_style_color ssc ON sd.id = ssc.style_id AND ssc.del_flag = '0' WHERE pb.pack_type = 'packDesign' AND pb.del_flag = '0' AND pbv.`status` = '1' AND pbv.`company_code` = #{companyCode} AND pb.company_code = #{companyCode} GROUP BY ssc.design_no, ssc.style_no ) bid ON cbgcd.design_no = bid.design_no_two AND cbgcd.style_no = bid.style_no_two WHERE bid.material_code IS NOT NULL
AND (bid.material_code LIKE concat('%',#{search},'%') OR bid.material_name LIKE concat('%',#{search},'%'))
AND bid.month = #{month}
AND bid.band_code = #{bandCode}
AND bid.month in
#{ month}
AND bid.band_code in
#{bandCode}
GROUP BY bid.material_code

SELECT pb.bulk_unit_use, pb.bulk_price, pb.loss_rate, pb.amount, pb.price_tax, pb.foreign_id, pbv.pack_type FROM t_pack_bom pb JOIN t_pack_bom_version pbv ON pbv.id = pb.bom_version_id WHERE pbv.foreign_id IN
#{id}
AND pbv.del_flag = '0' AND pbv.STATUS = '1' AND unusable_flag = 0

select count(id)
from t_pack_bom
where bom_version_id = #{bomVersionId}
