mapper.select:
select CONCAT(node, '-', status) as nodeStatus, count(1) as total
from t_pattern_making ${ew.customSqlSegment}
group by node, status

SELECT #{sampleType} AS sample_type, DATE_FORMAT( pmm.create_date, IF(#{weeklyMonth} = 'week','%y年%u周', '%y年%m月') ) AS year_week, min( pmm.create_date ) AS min_date, count( 1 ) AS num, SUM( pmm.requirement_num ) AS requirement_num_sum FROM ( SELECT sd.planning_category_id, pm.* FROM t_pattern_making pm LEFT JOIN t_sample_design sd ON pm.sample_design_id = sd.id LEFT JOIN t_planning_category pc ON sd.planning_category_id = pc.id WHERE pm.node = '打版任务' AND pm.company_code = #{companyCode} AND pm.create_date BETWEEN #{startTime} AND #{endTime}
AND pm.`status` IN
#{status}
AND
pc.category_ids LIKE concat('%',#{categoryId,jdbcType=VARCHAR},'%')
) pmm GROUP BY DATE_FORMAT( pmm.create_date, IF(#{weeklyMonth} = 'week','%y%u', '%y%m') ) ORDER BY year_week ASC;
