mapper.update:
update t_band
set update_date=now(),
update_name=#{updateName},
update_id=#{updateId}

    ,particular_year=#{particularYear}


    ,band_name=#{bandName}


    ,season=#{season}


    ,month=#{month}


    ,code=#{code}


    ,sort=#{sort}


    ,status=#{status}

where id = #{id}

mapper.select:
select *
from t_band
where company_code = #{companyCode}
  and del_flag = '0'

    and id in

        #{id}



    and band_name like concat('%',#{bandName},'%')


    and particular_year=#{particularYear}


    and season=#{season}


    and code=#{code}


    and status=#{status}

order by create_date desc
