update.foreach:
update t_group_tree
id = #{t.id,jdbcType=VARCHAR}, company_code = #{t.companyCode,jdbcType=VARCHAR}, group_name = #{t.groupName,jdbcType=VARCHAR}, name = #{t.name,jdbcType=VARCHAR}, code = #{t.code,jdbcType=VARCHAR}, sort = #{t.sort,jdbcType=INTEGER}, parent_id = #{t.parentId,jdbcType=VARCHAR}, parent_ids = #{t.parentIds,jdbcType=VARCHAR}, level = #{t.level,jdbcType=INTEGER}, is_leaf = #{t.isLeaf,jdbcType=CHAR}, create_id = #{t.createId,jdbcType=VARCHAR}, create_name = #{t.createName,jdbcType=VARCHAR}, create_date = #{t.createDate,jdbcType=TIMESTAMP}, update_id = #{t.updateId,jdbcType=VARCHAR}, update_name = #{t.updateName,jdbcType=VARCHAR}, update_date = #{t.updateDate,jdbcType=TIMESTAMP}, status = #{t.status,jdbcType=CHAR}, del_flag = #{t.delFlag,jdbcType=CHAR}, remarks = #{t.remarks,jdbcType=VARCHAR},
where id = #{t.id,jdbcType=BIGINT}

mapper.select:
