mapper.select:
select * from t_planning
where company_code = #{companyCode}
and del_flag = '0'

    and id in

        #{id}



    and planning_name like concat('%',#{planningName},'%')


    and particular_year=#{particularYear}


    and season=#{season}


    and gender=#{gender}


    and brand=#{brand}

order by create_date desc

mapper.update:
update t_planning
set update_date=now(),
update_name=#{updateName},
update_id=#{updateId}

    ,particular_year=#{particularYear}


    ,planning_name=#{planningName}


    ,season=#{season}


    ,month=#{month}


    ,wave_band_id=#{waveBandId}


    ,brand=#{brand}


    ,gender=#{gender}


    ,style_num=#{style_num}


    ,color_num=#{color_num}

where id = #{id}
