mapper.select:
select t.*,g.name as groupName from t_tag as t left join t_group_tree as g on g.id = t.group_id
where t.company_code = #{companyCode}
and t.del_flag ='0'

    and t.id in

        #{id}



    and t.tag_name like concat('%',#{tagName},'%')


    and t.group_id=#{groupId}


    and t.create_id=#{createId}

order by t.create_date desc

select id
from t_tag
where group_id = #{groupId}
  and tag_name = #{tagName}
  and del_flag = '0'

delete.if:
update t_tag set del_flag='1',
update_date=now(),
update_name=#{updateName},
update_id=#{updateId}
where id in

    #{id}

mapper.update:
update t_tag
set update_date=now(),
update_name=#{updateName},
update_id=#{updateId},
remarks=#{remarks}

    ,tag_name=#{tagName}


    ,group_id=#{groupId}

where id = #{id}
