mapper.select:
select * from t_tag
where company_code = #{companyCode}
and del_flag ='0'

    and id in

        #{id}



    and tag_name like concat('%',#{tagName},'%')


    and group_id=#{groupId}


    and create_id=#{createId}

order by create_date desc

select id
from t_tag
where group_id = #{groupId}
  and tag_name = #{tagName}

delete.if:
update t_tag set del_flag='1',
update_date=now(),
update_name=#{updateName},
update_id=#{updateId}
where id in

    #{id}

mapper.update:
update t_tag
set update_date=now(),
update_name=#{updateName},
update_id=#{updateId}

    ,tag_name=#{tagName}


    ,group_id=#{groupId}

where id = #{id}
