select s.id as 'season.id', s.name as 'season.name', s.year as 'season.year', s.season as 'season.season', s.month as 'season.month', b.id as 'band.id', b.name as 'band.name', b.sex as 'band.sex', b.band as 'band.band', b.devt_type as 'band.devtType', b.channel as 'band.channel', b.create_id as 'band.createId', b.create_name as 'band.createName' from t_planning_band b,t_planning_season s
