mapper.select:
SELECT b.band_code as dimension , count(1) as total FROM t_planning_band b JOIN t_planning_category_item ci ON ( b.id = ci.planning_band_id ) LEFT JOIN t_sample_design sd ON ( sd.planning_category_item_id = ci.id and sd.del_flag='0') WHERE b.del_flag = '0' AND ci.del_flag = '0' and ci.prod_category is not null and ci.prod_category <> '' and b.band_code is not null and b.band_code <> ''
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
group by dimension

select planning_season_id as id,count(id) as total from t_planning_category_item WHERE del_flag = '0'
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
group by planning_season_id

select planning_channel_id as id,count(id) as total from t_planning_category_item WHERE del_flag = '0'
${ ew.customSqlSegment.startsWith("WHERE")?ew.customSqlSegment.replace("WHERE","AND "):ew.customSqlSegment}
group by planning_channel_id
