mapper.select:
SELECT s.id, s.company_code, s.images, s.`status`, s.style_name, s.design_no, s.pattern_making_code, s.sample_type, s.category_name, s.season, s.customer_no, SUM(si.count), s.type, s.complete_status, s.examine_status, s.from_name, s.create_name, s.create_date, s.update_name, s.update_date, s.remarks FROM t_sample s JOIN t_sample_item si ON si.sample_id = s.id LEFT JOIN t_sample_circulate_item sci ON sci.sample_item_id = si.id LEFT JOIN t_sample_circulate sc ON sc.id = sci.sample_circulate_id
s.company_code = #{companyCode}
and s.type = #{type}
AND s.`status` = #{status}
AND si.from_type = #{fromType}
AND si.position_id = #{positionId}
-- 剩余1天 AND (sc.return_date IS NULL and TO_DAYS(sc.expect_return_date) - TO_DAYS(NOW()) = 1)
-- 剩余2天 AND (sc.return_date IS NULL and TO_DAYS(sc.expect_return_date) - TO_DAYS(NOW()) = 2)
-- 剩余3天 AND (sc.return_date IS NULL and TO_DAYS(sc.expect_return_date) - TO_DAYS(NOW()) = 3)
-- 剩余7天 AND (sc.return_date IS NULL and TO_DAYS(sc.expect_return_date) - TO_DAYS(NOW()) = 7)
-- 超过7天 AND (sc.return_date IS NULL and TO_DAYS(sc.expect_return_date) - TO_DAYS(NOW()) > 7)
-- 未逾期 AND (sc.return_date IS NULL and TO_DAYS(NOW()) - TO_DAYS(sc.expect_return_date) <= 0)
-- 逾期 AND (sc.return_date IS NULL and TO_DAYS(NOW()) - TO_DAYS(sc.expect_return_date) > 0)
AND (s.design_no like concat('%',#{search,jdbcType=VARCHAR},'%') OR s.customer_no like concat('%',#{search,jdbcType=VARCHAR},'%') OR si.`code` like concat('%',#{search,jdbcType=VARCHAR},'%'))
GROUP BY s.id ORDER BY s.create_date DESC
