mapper.select:
SELECT t.*, t1.sample_design_pic, t1.color_name,
t1.color_specification, t1.sales_type, t1.is_trim, t1.manufacturer, t1.manufacturer_no, t1.manufacturer_color,
t2.style_name, t2.design_no, t2.category_name, t2.prod_category1st, t2.prod_category, t2.prod_category2nd, t2.prod_category3rd, t2.brand, t2.band_code, t2.year, t2.season, t2.month, t2.sex, t2.devt_type, t2.main_materials, t2.rd_mat, t2.designer, t2.designer_id FROM t_sample_style_group t LEFT JOIN t_sample_style_color t1 ON t.style_no = t1.style_no LEFT JOIN t_sample_design t2 on t1.design_no = t2.design_no WHERE t.company_code = #{companyCode} and t.del_flag = '0' and t.id = #{id}
